mapper.select:
select * from(
select  rownum rnum, nickname, user_idx, bd_idx, bd_title, reg_date, view_count, rec_count, content ,category from(
	select m.nickname, m.user_idx, b.bd_idx, b.bd_title, b.reg_date, b.view_count, b.rec_count, b.content ,b.category
	from board b join "USER" m on(b.user_idx = m.user_idx)

		category = #{commandMap.category}
		and ${commandMap.option} like '%'||#{commandMap.keyword}||'%'


	order by ${commandMap.sort} desc
	))where rnum between ${((pageUtil.curPage-1) * pageUtil.cntPerPage) + 1} and ${pageUtil.curPage * pageUtil.cntPerPage}

select count(*) from(

	select m.nickname, b.bd_idx, b.bd_title, b.reg_date, b.view_count, b.rec_count, b.content ,b.category
	from board b join "USER" m on(b.user_idx = m.user_idx)

		category = #{commandMap.category}
		and ${commandMap.option} like '%'||#{commandMap.keyword}||'%'


	)
