mapper.select:
select * from( select rownum rnum ,cm_cnt, nickname, user_idx, bd_idx, bd_title, reg_date, view_count, rec_count, content ,category from (select count(c.bd_idx) cm_cnt, b.nickname, b.user_idx, b.bd_idx, b.bd_title, b.reg_date, b.view_count, b.rec_count, b.content ,b.category from board b left join board_comment c on(b.bd_idx = c.bd_idx)
category = #{commandMap.category} and ${commandMap.option} like '%'||#{commandMap.keyword}||'%'
group by b.bd_idx,b.nickname, b.user_idx, b.bd_idx, b.bd_title, b.reg_date, b.view_count, b.rec_count, b.content ,b.category order by ${commandMap.sort} desc ))where rnum between ${((pageUtil.curPage-1) * pageUtil.cntPerPage) + 1} and ${pageUtil.curPage * pageUtil.cntPerPage}

select count(*) from board_comment



	category = #{commandMap.category}
	and ${commandMap.option} like '%'||#{commandMap.keyword}||'%'

select * from (select rownum rnum, cm_idx, bd_idx,cm_content,reg_date, cm_rec_count, user_idx, nickname, pr_idx, cm_type , bd_title, category , origin_tit, origin_nick from (select c.cm_idx, c.bd_idx, c.cm_content,c.reg_date, c.cm_rec_count, c.user_idx, c.nickname, c.pr_idx, c.cm_type, b.bd_title, b.category , c1.cm_content origin_tit, c1.nickname origin_nick from board_comment c join board b on(c.bd_idx = b.bd_idx) join board_comment c1 on(c.pr_idx = c1.cm_idx)
category = #{commandMap.category} and ${commandMap.option} like '%'||#{commandMap.keyword}||'%'
order by ${commandMap.sort} desc ))where rnum between ${((pageUtil.curPage-1) * pageUtil.cntPerPage) + 1} and ${pageUtil.curPage * pageUtil.cntPerPage}
