mapper.select:
  		select count(*) from board_comment
  		where user_idx = #{userIdx}
  		
			and cm_content like '%'||#{keyword}||'%'

  		select * 
  		from (select *, rownum 
  		from (select * from board_comment where user_idx = #{searchSet.userIdx} 
		
			and cm_content like '%'||#{searchSet.keyword}||'%' 
		
  		order by cm_idx desc)) 
  		where rownum between ${((pageUtil.curPage-1) * pageUtil.cntPerPage) + 1} 
  		and ${pageUtil.curPage * pageUtil.cntPerPage}
